package s0330;

public class Exam7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=2;i<=10;i+=2)
			System.out.print(i + "\t");
		System.out.println();
		
		for(int i=0;i<=20;i+=5)
			System.out.print(i+"\t");
		System.out.println();
		
		for(int i=10;i>=4;i-=2)
			System.out.print(i+"\t");
		System.out.println();
	}

}
